import React, { Component } from 'react'
import axios from 'axios'



class PostList extends Component {

    constructor(props) {
        super(props)

        this.state = {
            posts: [],
            errorM
        }
    }

    componentDidMount() {
        axios.get('https://jsonplacehoflder.typicode.com/posts').then(
            Response => {
                console.log(Response)
                this.setState({
                    posts : Response.data
                })
            }
        ).catch(error => {
            console.log(error)
            this.setState({
                errorM:"Error retriving data"
            })
        })
    }

    render() {
        const {posts} = this.state
        return (
            <div>
                <h1>List of Posts</h1>
                {
                    posts.length ?
                    posts.map(post => <div key={post.id} > {post.title} </div>)
                    :
                    null
                }
            </div>
        )
    }
}

export default PostList
