import React, { useReducer } from 'react'


const initialState = {
    firstCounter: 0,
    secondCounter: 10
}
const reducer = (state, action) => {
    switch (action.type) {
        case 'increment':
            return { firstCounter: state.firstCounter + action.value }
            break;
        case 'Decrement':
            return { firstCounter: state.firstCounter - action.value }
            break;
            case 'increment2':
                return { secondCounter: state.secondCounter + action.value }
                break;
            case 'Decrement2':
                return { secondCounter: state.secondCounter - action.value }
                break;
        case 'reset':
            return initialState
        default:
            return state
            break;
    }
}
export default function ReducerCount1() {
    const [count, dispatch] = useReducer(reducer, initialState)
    return (
        <div>
            <h2>Current Value {count.firstCounter}</h2>
            <button onClick={() => dispatch({ type: 'increment', value: 1 })} >Increment</button>
            <button onClick={() => dispatch({ type: 'Decrement', value: 1 })}  >Decrement</button>
            <button onClick={() => dispatch({ type: 'increment', value: 5 })} >Increment5</button>
            <button onClick={() => dispatch({ type: 'Decrement', value: 5 })}  >Decrement5</button>
            
            
            <button onClick={() => dispatch({ type: 'reset' })}  >Reset</button>

        </div>
    )
}
