import React, { Component } from 'react'

class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
            username: '',
            comments: '',
            topic: 'angular'
        }
    }


    handlerUsername = (event) => {

        this.setState({
            username: event.target.value
        })
    }

    handlerComments = (event) => {
        this.setState({
            comments: event.target.value
        })
    }
    handlerTopicChange = (event) => {
        this.setState({
            topic: event.target.value
        })
    }

    handlerSubmit = (event) =>{
        alert(this.state.username)
        alert(this.state.comments)
        alert(this.state.topic)

        alert(`I am  ${this.state.username} I have been ${this.state.comments} and my Favorite tool is ${this.state.topic}`)
   
    }
    render() {
        return (



            <div>
                <form onSubmit={this.handlerSubmit} >
                    <div>
                        <h1>Form Component</h1>
                        <label>Username</label>
                        <input type='value' value={this.state.username} onChange={this.handlerUsername}></input>
                    </div>

                    <div>
                        <label>TextArea</label>
                        <textarea type="textarea" value={this.state.comments} onChange={this.handlerComments}
                        ></textarea>
                    </div>
                    <div>
                        <label>Topic</label>
                        <select value={this.state.topic} onChange={this.handlerTopicChange} >
                            <option value="react">React</option>
                            <option value="angular">Angular</option>
                            <option value="vue">Vue</option>
                        </select>
                    </div>
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}

export default Form
