import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import ClassCounter from './Components/ClassCounter';
import HookCounter from './Components/HookCounter';
import HookCounter2 from './Components/HookCounter2';
import HookCounter3 from './Components/HookCounter3';
import HookCounter4 from './Components/HookCounter4';
import HoockCounterEfffect from './Components/HoockCounterEfffect';
import HookMouse from './Components/HookMouse';
import MouseContainer from './Components/MouseContainer';
import IntervalHook from './Components/IntervalHook';
import DataFetching from './Components/DataFetching';
import DataFetchingById from './Components/DataFetchingById';
import ComponentC from './Components/ComponentC';

export const UserContext = React.createContext()

function App() {
    return (


      <div className="App" >
        <h1>
          Using Class Component
      </h1>
        <ClassCounter></ClassCounter>

        <h1>
          Using Hooks useState
      </h1>
        <HookCounter></HookCounter>


        <h1>
          Using Hooks useState /Reset/Increment/Decrement
      </h1>
        <HookCounter2></HookCounter2>
        <HookCounter3></HookCounter3>
        <HookCounter4></HookCounter4>
        <HoockCounterEfffect>

        </HoockCounterEfffect>
        <MouseContainer></MouseContainer>
        <IntervalHook></IntervalHook>
        <DataFetching></DataFetching>
        <DataFetchingById></DataFetchingById>


      <UserContext.Provider value={'Daniel'} >
          <ComponentC>

          </ComponentC>
      </UserContext.Provider>

      </div>



    )
  }

export default App;
