import React, { Component } from 'react'
import Input from './Input'

 class FocusInput extends Component {
constructor(props) {
    super(props)
    this.inputRefComponent = React.createRef()
    this.state = {
         
    }
}


callInputHandler = () =>{
    this.inputRefComponent.current.focusInput()
}
    render() {
        return (
            <div>
                <Input ref={this.inputRefComponent} ></Input>
                <button onClick={this.callInputHandler} >Focus Input</button>
            </div>
        )
    }
}

export default FocusInput
