import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';


const columns = [
    //name, capital, region,subRegion,population,area,timezone,nativeName, link
  { id: 'name', label: 'Name', minWidth: 100 },
  { id: 'capital', label: 'Capital', minWidth: 100 },
  { id: 'region', label: 'Region', minWidth: 100 },
  { id: 'subRegion', label: 'Sub Region', minWidth: 100 },
  { id: 'population', label: 'Population', minWidth: 100 },
  { id: 'area', label: 'Area', minWidth: 100 },
  { id: 'timezone', label: 'timezone', minWidth: 100 },
  { id: 'nativeName', label: 'native Name', minWidth: 100 },
  { id: 'link', label: 'Link Bandeira', minWidth: 100 },

];

function createData(name, capital, region,subRegion,population,area,timezone,nativeName,link) {
  return { name, capital, region, subRegion, population,area,timezone,nativeName,link};
}

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});


export default function CountriesAPI() {

    const [countries,setCountries] = useState([])

/*     useEffect(() => {
        axios.get('https://restcountries.eu/rest/v2').then(
            response => {
                setCountries(response.data)
                console.log({countries})
            } 
        ).catch(error =>{ console.log(error)
    }
        )
    },[])  */

//salert( dados)
let user = []


var dados = getData()

let rows = [] 
dados.then(function(dados){
    for (let index = 0; index < dados.length-1; index++) {
        rows.push(dados[index]);

    }

 })
 console.log(rows)

async function getData() {

        let response = await axios.get('https://restcountries.eu/rest/v2');
        user = await response.data
        console.log(user) 
        return user;
      }

  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };




  return (



    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>

  )


}
