import React, { Component } from 'react'

class Counter_ extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             count: 0
        }
    }
    
    clickedTimes = () =>{
        this.setState((previousState)=>{
            return {count: previousState.count + 1}
        })
    }
    render() {
        return (
            <div>
                {this.props.render(this.state.count,this.clickedTimes)}
            </div>
        )
    }
}

export default Counter_
