}

const downloadXML = () =>{
    {exportFromJSON({ data: props.mockdata, fileName: 'data', exportType: exportFromJSON.types.xml })}

}

const downloadCSV = () =>{
    {exportFromJSON({ data: props.mockdata, fileName: 'data', exportType: exportFromJSON.types.csv })}

}
  return (
      <div>
    <div >
    <Button color="inherit" onClick={downloadXLS} >Download no Formarto XLS</Button>

    <Button color="primary"  onClick={downloadCSV} >Download no Formarto CSV</Button>


    <Button color="primary" onClick={downloadXML} >Download no Formarto XML</Button>

    </div>

    </div>

  );
}
