import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Options from './Options';


const columns = [
  { id: 'name', label: 'Name', minWidth: 100 },
  { id: 'capital', label: 'Capital', minWidth: 100 },
  { id: 'region', label: 'Region', minWidth: 100 },
  { id: 'subRegion', label: 'Sub Region', minWidth: 100 },
  { id: 'population', label: 'Population', minWidth: 100 },
  { id: 'area', label: 'Area', minWidth: 100 },
  { id: 'timezone', label: 'timezone', minWidth: 100 },
  { id: 'nativeName', label: 'native Name', minWidth: 100 },
  { id: 'flag', label: 'Link Bandeira', minWidth: 100 },

];
let rows= []

function createData(name, capital, region,subRegion,population,area,timezone,nativeName,flag) {
  return { name, capital, region, subRegion, population,area,timezone,nativeName,flag};
}

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});


export default function CountriesAPI() {

    const [countries,setCountries] = useState([])

useEffect(() => {
        axios.get('https://restcountries.eu/rest/v2').then(
            response => {
                setCountries(response.data)
            } 
        ).catch(error =>{ 
            console.log(error)
    }
        )
    },[])   


var dados = getData()

dados.then(function(dados){
    for (let index = 0; index < dados.length-1; index++) {
        rows.push(createData(dados[index].name,dados[index].capital,dados[index].region,dados[index].subregion,dados[index].population,dados[index].area,dados[index].timezones[0],dados[index].nativeName,dados[index].flag.to));
    }

 })

 let Link = 'https://restcountries.eu/rest/v2'
async function getData() {

        let response = await axios.get('https://restcountries.eu/rest/v2');
        let user = await response.data
        return user;
      }

  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };



if(rows.length < 1 ){
return(
    <div>

        <h2>Looding... </h2>
    </div>
)
}else{
  return (
      <div>
        <div>
          <div className={classes.section4} ><Options mockdata={rows} ></Options></div>
          <h2></h2>
        </div>
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.id}>
                  {columns.map((column) => {
                    const value = row[column.id];
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : 
                        column.id === "flag" ?
                       <a src={value} alt={value} width="10" height="10"/>
                        : value
                        }
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
    </div>
  )

}
}
