import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';


let dados = [];
const columns = [
    //name, capital, region,subRegion,population,area,timezone,nativeName, link
  { id: 'name', label: 'Name', minWidth: 100 },
  { id: 'capital', label: 'Capital', minWidth: 100 },
  { id: 'region', label: 'Region', minWidth: 100 },
  { id: 'subRegion', label: 'Sub Region', minWidth: 100 },
  { id: 'population', label: 'Population', minWidth: 100 },
  { id: 'area', label: 'Area', minWidth: 100 },
  { id: 'timezone', label: 'timezone', minWidth: 100 },
  { id: 'nativeName', label: 'native Name', minWidth: 100 },
  { id: 'link', label: 'Link Bandeira', minWidth: 100 },

];

function createData(name, capital, region,subRegion,population,area,timezone,nativeName,link) {
  return { name, capital, region, subRegion, population,area,timezone,nativeName,link};
}

/* const rows = [

responseData.map(res => createData(res.name, 'IN', 1324171354, 3287263),)

]; */

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});


export default function CountriesAPI() {

    const [countries,setCountries] = useState([])

    useEffect(() => {
        axios.get('https://restcountries.eu/rest/v2/all').then(
            response => {
                setCountries(response.data)
                console.log({countries})
            } 
        ).catch(error =>{ console.log(error)
    }
        )
    },[]) 

 const rows = [

    countries.map(data => {
      createData(data.name, data.capital,data.region,data.subregion,data.population,data.area,data.timezone,data.nativename,data.flag)
    })


] 

console.log(rows)

/* let rows = [
    createData('India', 'IN', 1324171354, 3287263),
    createData('China', 'CN', 1403500365, 9596961),
    createData('Italy', 'IT', 60483973, 301340),
    createData('United States', 'US', 327167434, 9833520),
    createData('Canada', 'CA', 37602103, 9984670),
    createData('Australia', 'AU', 25475400, 7692024),
    createData('Germany', 'DE', 83019200, 357578),
    createData('Ireland', 'IE', 4857000, 70273),
    createData('Mexico', 'MX', 126577691, 1972550),
    createData('Japan', 'JP', 126317000, 377973),
    createData('France', 'FR', 67022000, 640679),
    createData('United Kingdom', 'GB', 67545757, 242495),
    createData('Russia', 'RU', 146793744, 17098246),
    createData('Nigeria', 'NG', 200962417, 923768),
    createData('Brazil', 'BR', 210147125, 8515767),
  ]; */

  console.log({countries})
  const classes = useStyles();
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };



  return (
    <Paper className={classes.root}>
      <TableContainer className={classes.container}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.alpha2Code}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
              return (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.name}>
                  {columns.map((column) => {
                    const value = row[column.name];
                    return (
                      <TableCell key={column.name} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={rows.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
