import React, { Component } from 'react'

 class EventBinding extends Component {
     constructor(props) {
         super(props)
     
         this.state = {
              message: 'Hello'
         }
     }

     clicHandler(){
         this.setState({
             message: 'bY'
         })
     }
     
    render() {
        return (
            <div>
                <div>{this.state.message}</div>
                <button onClick={this.clicHandler}>Click</button>
            </div>
        )
    }
}

export default EventBinding
