import React, { useState } from 'react';
import { withStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import CheckBoxOutlineBlankIcon from '@material-ui/icons/CheckBoxOutlineBlank';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';
import Convert from './Convert';

const GreenCheckbox = withStyles({
  root: {
    color: green[400],
    '&$checked': {
      color: green[600],
    },
  },
  checked: {},
})((props) => <Checkbox color="default" {...props} />);

export default function Options() {
    const [state, setState] = React.useState({
        checkedA: false,
        checkedB: false,
        checkedC: true,
      });
  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  const handleCovert = () =>{


    if(state.checkedA ==true){
        alert(state.checkedA)

    }
    if(state.checkedB ==true){
        alert('This is Cheeked B')
    }
    if(state.checkedC == true){
        alert('This is Checked C')
    }
    return <Convert></Convert>

}

  return (
      <div>
    <FormGroup row>
      <FormControlLabel
        control={<Checkbox checked={state.checkedA} onChange={handleChange} name="checkedA" />}
        label="XLS"
      />
      <FormControlLabel
        control={
          <Checkbox
            checked={state.checkedB}
            onChange={handleChange}
            name="checkedB"
            color="primary"
          />
        }
        label="CSV"
      />

      <FormControlLabel
        control={<GreenCheckbox checked={state.checkedG} onChange={handleChange} name="checkedC" />}
        label="XML"
      />

    </FormGroup>

    <div >
        <button onClick={handleCovert} color="primary">convert</button>
      </div>
    </div>

  );
}
